INSERT INTO `area_query_source` VALUES (118,28,'\0','2013-06-25 14:56:40','Prospects','2014-03-28 11:50:47','Prospects',76,'from customer_location_base_address  \ninner join customer_location_base on customer_location_base.id=customer_location_base_address.customer_location_id \ninner join work_flow_tracking on customer_location_base.work_flow_tracking_id=work_flow_tracking.id \ninner join work_flow on work_flow_tracking.work_flow_id=work_flow.id \ninner join area_meta_data on work_flow.area_meta_data_id=area_meta_data.id\ninner join customer_location_service on customer_location_service.customer_location_id = customer_location_base.id \ninner join service_type on service_type.id =  customer_location_service.service_type_id\ninner join customer_location_proposal on customer_location_proposal.customer_location_id = customer_location_base.id \nand customer_location_proposal.wf_status in (\'Survey\', \'Draft\')\nand customer_location_proposal.customer_location_service_id = customer_location_service.id\nleft outer join user on customer_location_base.primary_contact_id=user.id \nleft outer join customer_location_base_phone on customer_location_base_address.id=customer_location_base_phone.customer_location_base_phones_id \nleft outer join phone on customer_location_base_phone.phone_id=phone.id and (phone.type=\'W\') \nleft outer join user salesperson on customer_location_base.salesperson_id=salesperson.id \ninner join address on customer_location_base_address.address_id=address.id',82);
